import React,{useState} from "react";

function SneakerCard({sneaker,currentUser, handleComments}){
console.log("sneakerinfo:",sneaker)

  const [showForm, setShowForm] = useState(false);
  const [form, setForm] = useState({})
  const [showComments, setShowComments] = useState("")

  const addComment =(e)=>{
    e.preventDefault()
    fetch('http://localhost:3000/new_comment',{
      method: 'POST',
      headers:{
          'Content-Type': 'application/json'
      },
      body: JSON.stringify()
      }).then(res=>res.json())
      .then(sneaker=>{
      handleComments(sneaker)
    })
  }
// add the comment button
          const showAddCommentButton = () => {
            return (
                <>
                {showForm ? (<button onClick={() => setShowForm(!showForm)} className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded-full">Cancel</button>) : (<button onClick={() => setShowForm(!showForm)} className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded-full">Add A Comment</button>)}
                </>
            )
          }


// add the commentForm
const commentForm =()=>{
  return(
    <div>
      {showForm?
    <form classname="new-comment-form"
    onSubmit={addComment}>
      <input type="text" onChange={setShowComments}value="insert Comment"/>
      <button type="submit">Submit your Comments</button>
    </form>
    :
    null}
    </div>
  )
}



    return(

        <div className="Card">
          <h1> Sku:{sneaker.sku}</h1>
          <h3> Links: {sneaker.links.flightClub}</h3>
          <h3> Brand: {sneaker.brand}</h3>
          <h3>  Name: {sneaker.name}</h3>
          <h3>Colorway: {sneaker.colorway}</h3>
          <h3>Gender: {sneaker.gender}</h3>
          <h3> Silhouette: {sneaker.silhouette}</h3>
          <h3>ReleaseYear:{sneaker.releaseYear}</h3>
          <h3> ReleaseDate:{sneaker.releaseDate}</h3>
          <h3>RetailPrice:{sneaker.retailPrice}</h3>
          <h3>EstimatedMarketValue{sneaker.estimatedMarketValue}</h3>
          <h3>Story:{sneaker.story}</h3>

         </div>

  )
}

export default SneakerCard
