    const [showForm, setShowForm]= useState(false)


    fetch("http://localhost:3000/allsneakers")
    .then(r=>r.json()
    .then(console.log()))

        const onChangeHandler =(e)=>{
          setEndPoint(e.target.value)
        }


        const submitHandler =e =>{
          e.preventDefault()

        }






