import { useEffect, useState } from "react";



function Login(){
    const [userToLogin, updateUserToLoginInfo] = useState(
        {
            username: "",
            password:""
        } );


    const [loggedInUser,setLoggedInUser] = useState(null)

    useEffect(
    ()=>{fetch("http://localhost:3000/userInSession")
        .then(r=>r.json())
        .then(userAlreadyLoggedIn =>{
            setLoggedInUser(userAlreadyLoggedIn)
        })
    },[]
)
//if(!loggedInUser) return <Login setLoggedInUser={setLoggedInUser}/>
    const handleLoginSubmit=(synthEvent)=>{
    synthEvent.preventDefault()

    fetch("http://localhost:3000/login",
    {
    method: "POST",
    headers:{"Content-Type": "application/json"},
    body: JSON.stringify(userToLogin)
    }
    )
    .then(r=>r.json())
    .then(pleaseWorkForUser=>{
    console.log(pleaseWorkForUser)
    setLoggedInUser(pleaseWorkForUser)
    })

    }


    const handleChangeForUserToLogin=(synthEvent)=>{
    console.log(synthEvent)
    updateUserToLoginInfo({...userToLogin,[synthEvent.target.name]:synthEvent.target.value})
    }

    const handleLogout=()=>{
    fetch("http://localhost:3000/logout",{method:"Delete"})
    .then(r=>r.json())
    .then(deleteResponse=>{

        setLoggedInUser(null)
    })
    }




    return(
        <>

        <div className="Login">
         <div className="loginWrapper">
            <div className="loginLeft">
                <h3 className="loginLogo">Kicks r Us</h3>
                    <span className="loginDesc">Shop here for the latest sneakers</span>
                <div className="loginRight">
            <div className="loginBox">
        {
            loggedInUser ?
            (<> 
            <h2> You are signed as {loggedInUser.username}!</h2>

        <button onClick={handleLogout}> LogOut</button>
            </>)
            :
            <></>
        } 
        <h1>Login  to Sneakers-R-Us</h1>
        <form onSubmit={handleLoginSubmit}> 

        <input type="username"
        onChange={handleChangeForUserToLogin}
        name="username"/>


        <input type="password"
        onChange={handleChangeForUserToLogin} 
        name='password'/>

        <button type= "submit"
        name="submit"/>
                </form>
            </div>
        </div>
    </div>
    </div>
    </div>
    </>
    )
}



export default Login;
