import React,{useState,useEffect} from "react";
import './SneakerCard.css';

function SneakerCard({sneaker,currentUser, handleComments}){
console.log("sneakerinfo:",sneaker)

  const [showForm, setShowForm] = useState("")
  const [form, setForm] = useState({})
  const [showComments, setShowComments] = useState("")







// creating a  comment 
const handleSubmit=(synthEvent)=>{
  synthEvent.preventDefault()

  fetch("http://localhost:3000/new_comment",
  {
  method: "POST",
  headers:{"Content-Type": "application/json"},
  body: JSON.stringify()
  }
  )
  .then(r=>r.json())
  .then(pleaseWorkForComments=>{
  console.log(pleaseWorkForComments)
  handleComments(pleaseWorkForComments)
  })

  }

  const handleChangeForCommentSection=(synthEvent)=>{


  }
const commentForm =()=>{
  return(
    <form onSubmit={handleSubmit}>
      <input type="text" onChange={setShowComments}>Insert Comment</input>
      <button type="submit">Submit your Comments</button>
    </form>
  )
}
    //adding comment button
  const addCommentButton =() =>{
    return(
      <>
      {showForm? (<button onClick={()=> setShowForm(!showForm)}>cancel </button>):(<button onClick={()=>setShowForm(!showForm)}>Add comments</button>)}
      </>
    )
  }


      return(
        <div className="Card">
          <h1 className={sneaker.sku}></h1>
          <h3> Links: {sneaker.results.flightClub}</h3>
          <h3> Brand: {sneaker.brand}</h3>
          <h3>  Name: {sneaker.name}</h3>
          <h3>Colorway: {sneaker.colorway}</h3>
          <h3>Gender: {sneaker.gender}</h3>
          <h3> Silhouette: {sneaker.silhouette}</h3>
          <h3>ReleaseYear:{sneaker.releaseYear}</h3>
          <h3> ReleaseDate:{sneaker.releaseDate}</h3>
          <h3>RetailPrice:{sneaker.retailPrice}</h3>
          <h3>EstimatedMarketValue{sneaker.estimatedMarketValue}</h3>
          <h3>Story:{sneaker.story}</h3>

          <div className="text-center">
            {currentUser ? commentForm() : null}
          </div>
          {currentUser ? addCommentButton() : null}
        </div>
    )
}

export default SneakerCard;
