import Home from "./components/Home"
import './App.css';
import Login from './components/Login';
import {
 Routes,
 Route
} from 'react-router-dom';
import Navbar from "./components/Navbar";
import Signup from "./components/Signup";
import {useEffect, useState} from "react";

function App() {

  const [currentUser,setCurrentUser] = useState('');
  const [comment, setComments] = useState([]);

  const updateUser =(user)=> setCurrentUser(user)

  const [sneakerData, setSneakerData] = useState([]);
  const [errors, setErrors] = useState(false);

  useEffect(()=>{
    fetch('http://localhost:3000/allsneakers')
    .then(res=>{
      if(res.ok){
        res.json().then(setSneakerData)
      }else{
        res.json().then(data=> setErrors(data.error))
      }
    })
  },[])


useEffect(()=>{
  fetch('http://localhost:3000/comments')
  .then(res=>res.json())
  .then(data => setComments(data))
},[])



  useEffect(()=>{
    fetch('http://localhost:3000/me')
    .then(res=>{
      if(res.ok){
        res.json().then(user=>{
          setCurrentUser(user);
        })
      }else{
        res.json().then(data => setErrors(data.error))
      }
    })

  },[])

    const handleComments = (addedComment) => {
    setComments(oldComments =>[...oldComments, addedComment])
      }

      const getSneakers =()=>{
        fetch('/sneakers').then(res => res.json())
      }

  return(
    <div>

      <Navbar/>
      <Routes>
        <Route path="/" element={<Home sneakers={sneakerData} currentUser={currentUser} handleComments={handleComments}/>}/>
        <Route path="/login" element={<Login updateUser={updateUser}/>}/>
        <Route path="/signup" element={<Signup updateUser={updateUser}/>}/>
      </Routes>

    </div>
  );


}

export default App;
