})
  useEffect(()=>{
    fetch('/me')
    .then(res=>{
      if(res.ok){
        res.json().then(user=>{
          setCurrentUser(user);
        })
      }else{
        res.json().then(data => setErrors(data.error))
      }
    })

  },[])
  return(
    <div>

      <Navbar/>
      <Routes>
        <Route path="/" element={<Home sneakers={sneakerData} currentUser={currentUser}/>}/>
        <Route path="/login" element={<Login/>}/>
        <Route path="/signup" element={<Signup/>}/>
      </Routes>

    </div>
  );


}

export default App;
