import Home from "./components/Home"
import './App.css';
import Login from './components/Login';
import {
 Routes,
 Route
} from 'react-router-dom';
import Navbar from "./components/Navbar";
import Signup from "./components/Signup";
import {useEffect, useState} from "react";


function App() {
  //current logged in user state
  const [currentUser,setCurrentUser] = useState('');
  const [comments, setComments] = useState([]);

  const updateUser =(user)=> setCurrentUser(user)

// GET all sneakers from the API
  const [sneakerData, setSneakerData] = useState([]);

  const [errors, setErrors] = useState(false);

  useEffect(()=>{

    fetch('http://localhost:3000/all_sneakers')
    .then(res=>{
      if(res.ok){
        res.json().then(setSneakerData)
      }else {
        res.json().then(data => setErrors(data.error))
      }
    })
  },[])
//GET comments
   useEffect(()=>{
   fetch('http://localhost:3000/comments')
  .then(res=>res.json())
  .then(data => setComments(data))
  },[])


// currently logged in state user
    useEffect(()=>{
    fetch('http://localhost:3000/users')
    .then(res=>{
      if(res.ok){
        res.json().then(user=>{
          setCurrentUser(user);
        })
      }else{
        res.json().then(data => setErrors(data.error))
      }
     })

      },[])

      const handleComments =(newComments)=>{
      setComments(comments =>[...comments,newComments])
      }

    if(errors) return<h1>{errors} </h1>

  return(
    <div>
    {  currentUser ? <Route  path='/home' element={<Home sneakers={sneakerData} currsendComments={comments}/>} /> : <Route path="/" element={<Login updateUser={updateUser}/>}/> }
      <Navbar currentUser={currentUser} updateUser={updateUser}/>
      <Routes> 

      {/* <Route  path='/home' element={<Home sneakers={sneakerData} currentUser={currentUser} handleComments={handleComments} sendComments={comments}/>}/>
        <Route path="/" element={<Login updateUser={updateUser}/>}/> */}

       <Route path="/signup" element={<Signup updateUser={updateUser}/>}/>
      </Routes>

    </div>
  );


}

export default App;
